// Karty pilarów
const PillarCard = ({ title, icon, items, style = {} }) => {
  const { isMobile } = useResponsive();
  
  return (
    <div style={{ 
      width: '100%', 
      height: 'auto',
      background: 'white',
      boxShadow: '0px 15px 30px rgba(155, 85, 198, 0.30)',
      borderRadius: 12,
      border: '1px solid #F3F4F6',
      padding: '25px',
      display: 'flex',
      flexDirection: 'column',
      ...style
    }}>
      <div style={{ 
        display: 'flex', 
        alignItems: 'center',
        marginBottom: '24px'
      }}>
        <div style={{ 
          width: '64px', 
          height: '64px', 
          background: 'linear-gradient(90deg, #9B55C6 0%, #4A8DFF 100%)',
          borderRadius: 8,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'
        }}>
          {icon}
        </div>
        <div style={{ 
          marginLeft: '16px',
          color: '#1F2937',
          fontSize: '20px',
          fontWeight: '500',
          lineHeight: '20px'
        }}>
          {title}
        </div>
      </div>

      <div style={{ display: 'flex', flexDirection: 'column', gap: isMobile ? '18px' : '12px' }}>
        {items.map((item, index) => (
          <div key={index} style={{ display: 'flex', alignItems: 'flex-start' }}>
            <div style={{ marginRight: '12px', marginTop: '4px' }}>
              <BulletPoint />
            </div>
            <div style={{ 
              fontSize: '16px',
              color: '#1F2937',
              lineHeight: '16px',
              fontWeight: '400'
            }}>
              {item}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

